package com.sytanx.class3;

public class IfConditionDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number1=10;
		int number2=20;
		System.out.println("before If condition");
		if(number2>=number1) { //if you use true in the (), then anything inside the if condition will execute. If condition is false, the if condition lines will not be executed
				// if conditions operate on booleans
				System.out.println("I am inside If condition");
				System.out.println("I am inside If condition");
				System.out.println("I am inside If condition");
		}
		System.out.println("after If condition");
	}

}
